mapper.select:
select 

from t_investment_value
where productId = #{productId,jdbcType=VARCHAR}

    and (DATE_FORMAT(date,'%Y/%m/%d'))  >=  #{dateFrom,jdbcType=VARCHAR}


    and (DATE_FORMAT(date,'%Y/%m/%d'))  <=  #{dateTo,jdbcType=VARCHAR}

select 

from t_investment_value
where productId = #{productId,jdbcType=VARCHAR}

    and (DATE_FORMAT(date,'%Y%m%d'))  >=  #{dateFrom,jdbcType=VARCHAR}


    and (DATE_FORMAT(date,'%Y%m%d'))  <=  #{dateTo,jdbcType=VARCHAR}

select 

from t_investment_value a left join t_investment_master b on a.productId = b.id
where 1=1

    and b.productName like '%' || #{productName,jdbcType=VARCHAR} || '%'


    and (DATE_FORMAT(a.date,'%Y/%m/%d'))  >=  #{dateFrom,jdbcType=VARCHAR}


    and (DATE_FORMAT(a.date,'%Y/%m/%d'))  <=  #{dateTo,jdbcType=VARCHAR}

select count(1) from t_investment_value where 1=1
and b.productName like '%' || #{productName,jdbcType=VARCHAR} || '%'
and (DATE_FORMAT(date,'%Y/%m/%d')) >= #{dateFrom,jdbcType=VARCHAR}
and (DATE_FORMAT(date,'%Y/%m/%d')) <= #{dateTo,jdbcType=VARCHAR}

mapper.update:
update t_investment_value
set productId = #{productId,jdbcType=BIGINT},
  date = #{date,jdbcType=VARCHAR},
  netValue = #{netValue,jdbcType=DECIMAL}
where id = #{id,jdbcType=BIGINT}
